import com.eviware.soapui.model.testsuite.TestCaseRunContext
import net.sf.json.groovy.JsonSlurper

//Check if the response
assert context.response, 'Response is empty or null'

log.info("context.response: " + context.response)

def jsonResonponse = new JsonSlurper().parseText(context.response)

region = 0
regionName = jsonResonponse[region].name
regionUrl = jsonResonponse[region].apiUrl
if (regionUrl) {
	 regionUrl = regionUrl.replace("/v1.0/", "")
	 log.info("Found Region: " + regionName)	
	 log.info("Found region Url: " + regionUrl)
	 testRunner.testCase.testSuite.project.setPropertyValue( "hostUrl", regionUrl ) 

	 def ui = com.eviware.soapui.support.UISupport;
	 ui.showInfoMessage( "selected region is: " + regionUrl )

} else {
	  testrunner.fail("Failed to get region")
	  return
}

